mapper.select:
SELECT * FROM article
article_id = #{articleId}
ORDER BY sequence_no DESC

mapper.insert:
INSERT INTO article 
(
	article_id
	,group_id
	,sequence_no
	,posting_date
	,read_count
	,writer_name
	,password
	,title
	,content
)
VALUES
(
	SEQ_ARTICLE_ID_ARTICLE.nextval
	,#{groupId}
	,#{sequenceNo}
	,sysdate
	,0
	,#{writerName}
	,#{password}
	,#{title}
	,#{content}
)
